var MV = {state:'false', value:'mixed_vacc_status', html:'Mixed vaccination status'};
var UK = {state:'false', value:'unknown_vacc_status', html:'Unknown vaccination status'};

while (gr.next()){
	var y= [];


	if(gr.u_vacc_status != '' && gr.u_vacc_status != null){
		switch(gr.u_vacc_status.toString()){
			case OCV.value:
				OCV.state = 'true';
				break;
			case NV.value:
				NV.state = 'true';
				break;
			case MV.value:
				MV.state = 'true';
				break;
			case UK.value:
				UK.state = 'true';
		}
	}

	for(var i = 0; i < arr_length ; i++){
		var objKeys = Object.keys(t.column_labels);
		var field = objKeys[i];
		var col = t.column_labels.field;
		y.push({label: t.column_labels[field],  value: gr.getDisplayValue(field)});
	}

	t.related.push(y);

}
