sp_widget.script:
(function() {

  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.title = 'Lab Results';
	data.table = 'x_cria_livestock_t_lab_results_bruc';
	data.filter = 'session_id=' + gs.getSessionID();
	data.table_label = 'Lab Results';
	data.fields_array = [];
	data.view = '';
	data.action = undefined;
	data.updated = '';
	if(!data.title){
		data.title = data.table;
	}

	if (!data.fields) {
		if (data.view)
			data.fields = $sp.getListColumns(data.table, data.view);
		else
			data.fields = $sp.getListColumns(data.table);
	}

	data.column_labels = {};
	data.fields_array = data.fields.split(',');

	var gr = new GlideRecord(data.table);
	gr.addEncodedQuery(data.filter);
	gr.query();

		data.list = [];
		while (gr._next()) {
			var record = {};
			$sp.getRecordElements(record, gr, data.fields);
			record.sys_id = gr.getValue('sys_id');
			data.list.push(record);
		}

		// use GlideRecord to get field labels vs. GlideRecordSecure
	var grForLabels = new GlideRecord(data.table);
	for (var i in data.fields_array) {
		var field = data.fields_array[i];
		var ge = grForLabels.getElement(field);
		if (ge == null)
			continue;

		data.column_labels[field] = ge.getLabel();
	}


})();
