sys_script.script:
(function executeRule(current, previous /*null when async*/) {
		var test_interp = {
			susp: 0,
			react: 0,
			neg: 0,
			pos: 0,
			total: 0
		};

		var gr = new GlideRecord('x_cria_livestock_t_lab_results_bruc_market_cattle');
		var result = '';
		gr.addQuery('parent', current.sys_id);
		gr.query();
		while (gr.next()){
			if (gr.test_interp.getDisplayValue() == 'Suspect'){
	test_interp.susp++;
}
if (gr.test_interp.getDisplayValue() == 'Reactor'){
	test_interp.react++;
}
if (gr.test_interp.getDisplayValue() == 'Neg'){
	test_interp.neg++;
}
if (gr.test_interp.getDisplayValue() == 'Pos'){
	test_interp.pos++;
}
		test_interp.total++;	
		}
	current.positive = test_interp.pos+'';
	current.negative = test_interp.neg+'';
	current.reactor = test_interp.react+'';
	current.suspect = test_interp.susp+'';
	current.total_tested = test_interp.total+'';

})(current, previous);
