sys_script_client.script:
function onLoad() {
	if(g_form.getValue('draft') == 'true'){
		g_form.showFieldMsg('draft','To officially submit the form to ISDA, uncheck this box and click the "Save (Ctrl+S)" button.','info');
		g_form.showFieldMsg('vacc_date','I certify (1) that I have vaccinated with an approved vaccine; officially tattooed and eartagged, or otherwise officially, individually identified all animals listed hereon as prescribed by the Brucellosis UM and R, and recorded all information as prescribed by State regulations; and (2) when payment is claimed at the programs expense, no payment has been or will be received from any other source.','error');
	}
}
