sys_script_client.script:
function onLoad() {
	if(g_form.getValue('draft') == 'true'){
		g_form.showFieldMsg('draft','To officially submit the form to ISDA, uncheck this box and click the "Save (Ctrl+S)" button.','info');
		g_form.showFieldMsg('u_vet_signature','I certify that this test was made and read by me on each of the animals identified above on the dates and with the results as entered in appropriate spaces, and when payment is claimed at program expense, no payment has been or will be received from any other source.','error');
	}
}
